                req.session.nickname = nickname;
                req.session.save(function () {
                    res.redirect(`/`);
                });
            }
            else{
                console.log('토큰!', token);
                req.session.type = platform_type;
                req.session.email = email;
                req.session.is_logined = true;      // 세션 정보 갱신
                req.session.nickname = nickname;
                req.session.save(function () {
