//     res.redirect('/auth/login');
//     return false;
//   } else {                                      // 로그인 되어있으면 메인 페이지로 이동시킴
//     res.redirect('/review');
//     return false;
//   }
// })



// // 인증 라우터
app.use('/auth', authRouter);
app.use('/image', express.static('image'))
