            response.send("<script type=\"text/javascript\">alert(\"\uC785\uB825\uB41C \uBE44\uBC00\uBC88\uD638\uAC00 \uC11C\uB85C \uB2E4\uB985\uB2C8\uB2E4.\"); \n                        document.location.href=\"/auth/register\";</script>");
