"use strict";

var express = require("express");
var router = express.Router();
var authCheck = require("../lib_login/authCheck.js");
var db = require("../db");
var sanitizeHtml = require("sanitize-html");
var Tokens = require("csrf");
var tokens = new Tokens();
var template = require("./template");
var multer = require("multer");
var fs = require("fs");
var fileFilter = function fileFilter(req, file, cb) {
  var typeArray = file.mimetype.split('/');
  var fileType = typeArray[1];
  if (fileType == 'jpg' || fileType == 'png' || fileType == 'jpeg' || fileType == 'gif' || fileType == 'webp') {
    req.fileValidationError = null;
    cb(null, true);
  } else {
    req.fileValidationError = "jpg,jpeg,png,gif,webp 파일만 업로드 가능합니다.";
    cb(null, false);
  }
};
var storage = multer.diskStorage({
  //파일저장
  destination: function destination(req, file, cb) {
    cb(null, "uploads");
  },
  filename: function filename(req, file, cb) {
    cb(null, Date.now() + '-' + file.originalname);
  }
});
var upload = multer({
  storage: storage,
  fileFilter: fileFilter,
  limits: {
    fileSize: 3 * 1024 * 1024
  }
});
router.get("/", function (request, response) {
  request.session.secret = tokens.secretSync();
  var token = tokens.create(request.session.secret);
  if (!tokens.verify(request.session.secret, token)) {
    throw new Error("invalid token!");
  } else {
    if (request.session.is_logined != true) {
      return response.redirect("/auth/login");
    }
  }
  request.session.secret = tokens.secretSync();
  var token = tokens.create(request.session.secret);
  if (!tokens.verify(request.session.secret, token)) {
    throw new Error("invalid token!");
  } else {
    db.query("SELECT * FROM Student", function (error, Reviews) {
      var email = request.session.email;
      db.query("SELECT * FROM Instructor WHERE Email_Address = ? ", [email], function (error2, Instructor) {
        if (error2) {
          throw error2;
        }
        if (Instructor !== undefined && Instructor.length > 0) {
          //선생일경우
          return response.redirect("/myinfo/instructor");
        } else {
          db.query("SELECT * FROM Student", function (error, Reviews) {
            var email = request.session.email;
            db.query('SELECT * FROM Student WHERE Email_Address = ? ', [email], function (error2, result) {
              db.query("select * from Section as S left join Instructor_Time_Date as ITD on S.Course_ID = ITD.Course_ID and S.TIME_ID = ITD.TIME_ID and S.DATE_ID = ITD.DATE_ID and ITD.Teacher_ID = S.Teacher_ID left join Course as C on S.Course_ID = C.Course_ID left join Instructor_TIME as IT on S.TIME_ID = IT.TIME_ID left join  Instructor_DATE as ID on S.DATE_ID = ID.DATE_ID where Student_ID = ?;\n                          select * from Payment as P left join Instructor_Time_Date as ITD on P.Course_ID = ITD.Course_ID and P.TIME_ID = ITD.TIME_ID and P.DATE_ID = ITD.DATE_ID and ITD.Teacher_ID = P.Teacher_ID left join Course as C on P.Course_ID = C.Course_ID left join Instructor_TIME as IT on P.TIME_ID = IT.TIME_ID left join  Instructor_DATE as ID on P.DATE_ID = ID.DATE_ID where Student_ID = ?;\n                ", [result[0].Student_ID, result[0].Student_ID], function (error2, results) {
                if (error2) throw error2;
                var lecture_list = template.lecture_list(results[0]);
                var payment_list = template.payment_list(results[1]);
                response.render('my_page_root', {
                  Title: '강의 리스트',
                  authCheck: authCheck.statusUI(request, response),
                  Lecture_List: lecture_list,
                  Payment_List: payment_list,
                  Name: result[0].Name,
                  Password: result[0].Password,
                  Phone_Number: result[0].Phone_Number,
                  Address: result[0].Address,
                  Email_Address: result[0].Email_Address,
                  Point: result[0].Point
                });
              });
            });
          });
        }
      });
    });
  }
});
