"use strict";

var sanitizeHtml = require('sanitize-html');
module.exports = {
  list: function list(topics) {
    var list = '';
    var i = 0;
    while (i < topics.length) {
      list = list + "<tr>
                    <td><span id="number".concat(i, "" href="/review/page/").concat(topics[i].Review_ID, "">").concat(topics[i].Review_ID, "</span></td>
                    <td>
                      ").concat(topics[i].Order_subject, "
                    </td>
                    <td>
                      <a href="/review/page/").concat(topics[i].Review_ID, "">
                      ").concat(topics[i].Title, "
                      </a>
                    </td>
                    <td>").concat(topics[i].Create_at, "</td>
                    <td id="name").concat(i, "" >").concat(topics[i].Name, "</td>
                    <td>
                      <div style="display:flex;justify-content : center;flex-direction: column;">
                        <button id="p_button").concat(i, "" style="border: none; background-color:transparent;"onclick="javascript:review_list.recommand_change_num(").concat(i, ");"><i class="bi bi-heart fs-3 text-primary"></i></button>
                        <span name="p_num").concat(i, "" id="p_num").concat(i, "" class="p_num">").concat(topics[i].Recommand_Count, "</span>
                      </div>
                    </td>
                    </tr>
                    ");
      i = i + 1;
    }
    list = list + '';
    return list;
