: '").concat(result[0][0].Subject, "',\n          //\uACB0\uC81C\uCC3D\uC5D0\uC11C \uBCF4\uC5EC\uC9C8 \uC774\uB984\n          amount: 100,//result_price,\n          //\uAC00\uACA9 \n          buyer_email: '").concat(result[1][0].Email_Address, "',\n          buyer_name: '").concat(result[1][0].Name, "',\n          buyer_tel: '").concat(result[1][0].Phone_Number, "',\n          buyer_addr: '").concat(result[1][0].Address, "',\n          buyer_postcode: '").concat(result[1][0].Recommand_ID, "',\n          /*  \n              \uBAA8\uBC14\uC77C \uACB0\uC81C\uC2DC,\n              \uACB0\uC81C\uAC00 \uB05D\uB098\uACE0 \uB79C\uB529\uB418\uB294 URL\uC744 \uC9C0\uC815 \n              (\uCE74\uCE74\uC624\uD398\uC774, \uD398\uC774\uCF54, \uB2E4\uB0A0\uC758 \uACBD\uC6B0\uB294 \uD544\uC694\uC5C6\uC74C. PC\uC640 \uB9C8\uCC2C\uAC00\uC9C0\uB85C callback\uD568\uC218\uB85C \uACB0\uACFC\uAC00 \uB5A8\uC5B4\uC9D0) \n              */\n      }, function (rsp) {\n        console.log('rsp',rsp);\n        if(rsp.status == 'paid' && rsp.paid_amount == 100){\n          if (rsp.success) {\n              var msg = '\uACB0\uC81C\uAC00 \uC644\uB8CC\uB418\uC5C8\uC2B5\uB2C8\uB2E4.';\n              msg += '\uACE0\uC720ID : ' + rsp.imp_uid;\n              msg += '\uC0C1\uC810 \uAC70\uB798ID : ' + rsp.merchant_uid;\n              msg += '\uACB0\uC81C \uAE08\uC561 : ' + rsp.paid_amount;\n              msg += '\uCE74\uB4DC \uC2B9\uC778\uBC88\uD638 : ' + rsp.apply_num;\n              alert(msg);\n              window.location.href = 'http://localhost:3000/payment/complete';\n          } else {\n              var msg = '\uACB0\uC81C\uC5D0 \uC2E4\uD328\uD558\uC600\uC2B5\uB2C8\uB2E4.';\n              msg += '\uC5D0\uB7EC\uB0B4\uC6A9 : ' + rsp.error_msg;\n              alert(msg);\n          }     \n        }else{\n          alert(\"\uC704\uC870\")\n        }\n      });\n    }\n        </script>\n\n\t\t\t"), '', '', authCheck.statusUI(req, res));
